import { ReactElement, useEffect, useState } from "react"
import { format, getTime } from 'date-fns'

import Loader from 'react-loader-spinner'


interface Props {
    data: any
}


const WeekGlance= ({data}: Props) => {
    const a = data[0].estimatedArrival
    const b = Date.parse(data[0].estimatedArrival)
    const today:any = format(new Date(), "MM/dd/yy")
    const todaysIndex: number  = data.findIndex((e:any) =>  e.estimatedArrival === today)
    data.sort((a:any,b:any)=>{
        const da:any = Date.parse(a.estimatedArrival)
        const db:any = Date.parse(b.estimatedArrival)
        return da-db
    })

    
    function weekSet (){   
        data.map((e:any,o:number)=>(
            <div key={o}>{e.estimatedArrival === today && <div>{e.id}</div>}  </div>
              
        )) 
        let week = []
        for (let i:number = 0; i <= 6 ; i ++){
            week[i] = (
                <div>
                {data[todaysIndex ].id}
                {console.log(data[todaysIndex])}
                {data.map((e:any,o:number)=>(
            <div key={o}>{e.estimatedArrival === today && <div>{e.id}</div>}  </div>
              
        )) }
                </div>
            )
        }
        return week
    }

    return (
        <div style={{display: "flex", padding: "5px", margin:'5px'}}>
           {weekSet()}
           
        </div>
    )
}

export default WeekGlance;
