        //             y = x.shipments
        //             console.log(y)                    
        //         }

        //     }
        //     fetch()

        // }, [])

        const [fetchShipmentsResult, setFetchShipmentsResult] = useState<FetchShipmentsResult | LoadingResult>(INITIAL_RESULT)
        const [ShipmentsResult, setShipmentsResult] = useState<FetchShipmentsResult>(null)

        useEffect(() => {
            fetchShipments()
                .then(result => {
                    setFetchShipmentsResult(result)
                    setShipmentsResult(result as SuccessResult)
                })
        }, [])

        let component: ReactElement

        console.log(ShipmentsResult.shipments)

        switch (fetchShipmentsResult.status) {
            case 'SUCCESS':
                component = <WeekGlance data={'1'} />

                break;
            case 'LOADING':
                component = <Box className={classes.loader}>
                    <Loader type="Grid" color={theme.palette.primary.main} />
                </Box >
                break
            case 'ERROR':
                component = <p>Error</p>
                break
        }

        return component
    }
