import { ReactElement, useEffect, useState } from "react"
import { format, getTime } from 'date-fns'
import '../App.css'
import Loader from 'react-loader-spinner'
import { da } from "date-fns/locale"


interface Props {
    data: any
}


const WeekGlance= ({data}: Props) => {
    const a = data[0].estimatedArrival
    const b = Date.parse(data[0].estimatedArrival)
    const today:any = format(new Date(), "MM/dd/yy")
    const todayCalc:any = new Date()

    data.sort((a:any,b:any)=>{
        const da:any = Date.parse(a.estimatedArrival)
        const db:any = Date.parse(b.estimatedArrival)
        return da-db
    })

    function weekSet (){   
        // data.map((e:any,o:number)=>(
        //     <div key={o}>{e.estimatedArrival === today && <div>{e.id}</div>}  </div>
        // )) 
        let week = []

        for (let i:number = 0; i <= 6 ; i ++){
            i===0 ? todayCalc : todayCalc.setDate(todayCalc.getDate() + 1);
            const todayDynamic = format(todayCalc, "MM/dd/yy")
            deliveryDay = data.filter((e:any,o:number)=>(
                e.estimatedArrival === todayDynamic 
            )) 

            let dayOftheWeek = new Date(todayDynamic).getDay()
            week[i] = (
                <div  key={i} style={{minWidth: "10%", height: "min-content", margin:"5px 10px 5px 0", padding:"5px 10px 5px 0", borderRight: "4px solid #80baac", cursor: "pointer" }}>
                    <div style={{display:"flex", justifyContent: "space-between",margin:"5px", padding:"5px", alignItems: "center", fontSize: "large", color: "gray"}}>
                      
                        <div style={{display:"flex", flexDirection: "column"}}>
                            <h3 style={{margin:"0 5px 0 0"}}>{dayOftheWeek}</h3>
                            {todayDynamic}
                        </div>
                        <div style={{fontSize: "x-large", display: "grid", color: "#05a82b", margin:"0 0 0 10px"}}>
                            {deliveryDay.length}
                        </div>
                    </div>
                    <div style={{display:"flex" , flexDirection: "row",}}>
                        {
                            data.map((e:any,o:number)=>(
                                    <div key={o}  >
                                        {
                                            e.estimatedArrival === todayDynamic 
                                            && 
                                            <div 
                                                className="daySlot"
                                                style={{display:"flex" , flexDirection: "row", margin:"0 5px", padding:"5px", height: "1vh", aspectRatio: "1/1" , borderRadius: "360px", backgroundColor: "gray" }}>
                                                {}
                                            </div>
                                        }  
                                    </div>
                            )) 
                        }
                    </div>
                </div>
            )
        }
        return week
    }

    return (
        <div className="small WeekGlance" style={{display: "flex", flexWrap: "wrap",  justifyContent:"space-between", padding:"20px"}}>
           {weekSet()}
        </div>
    )
}

export default WeekGlance;
