import { format } from 'date-fns'
import '../App.css'
import StatusColor from './StatusColor'
import Upcoming from './Upcoming'
import SelectionDetails from './SelectionDetails'


interface Props {
    data: any
}


const WeekGlance= ({data}: Props) => {
    const a = data[0].estimatedArrival
    const b = Date.parse(data[0].estimatedArrival)
    const today:any = format(new Date(), "MM/dd/yy")
    const todayCalc:any = new Date()

    data.sort((a:any,b:any)=>{
        const da:any = Date.parse(a.estimatedArrival)
        const db:any = Date.parse(b.estimatedArrival)
        return da-db
    })

    function weekSet (){   
        let week = []
        let deliveryDay 

        for (let i:number = 0; i <= 6 ; i ++){
            i===0 ? todayCalc : todayCalc.setDate(todayCalc.getDate() + 1);
            const todayDynamic = format(todayCalc, "MM/dd/yy")
            deliveryDay = data.filter((e:any,o:number)=>(
                e.estimatedArrival === todayDynamic 
            )) 

            let dayOftheWeek:number = new Date(todayDynamic).getDay()
            let dayOftheWeekString = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", 'Sunday']
           
            week[i] = (
                <div  key={i} style={{minWidth: "10%", margin:"5px 10px 5px 0", padding:"5px 10px 5px 0", borderRight: "4px solid #80baac", cursor: "pointer" }}>
                    <div style={{display:"flex", justifyContent: "space-between",margin:"5px", padding:"5px", alignItems: "center", fontSize: "large", color: "black", opacity:'70%'}}>
                        <div style={{display:"flex",flexDirection: "column"}}>
                            <h3 style={{margin:"0 5px 0 0"}}>
                                {dayOftheWeekString[dayOftheWeek]}
                            </h3>
                            <div style={{fontSize:"small", opacity:'40%',}}>{todayDynamic}</div>
                        </div>
                        <div style={{fontSize: "x-large", display: "grid", color: "#05a82b", margin:"0 0 0 10px"}}>
                            {deliveryDay.length}
                        </div>
                    </div>
                    <div style={{display:"flex" , flexDirection: "row",}}>
                        {
                            deliveryDay.map((e:any,o:number)=>{
                                return (
                                    <div key={o}>
                                            <StatusColor data={e}/>
                                            
                                    </div>
                            )}) 
                        }
                    </div>
                </div>
            )
        }
        return week
    }

    return (
        <div className='WeekGlance' >
            <div className="WeekGlanceDays widget  " >
            {weekSet()}
            </div>
            <div className="WeekDetails" >
                <div className='SelectionDetails widget'>
                    <SelectionDetails data={data}/>
                </div>
                <div className='Upcoming widget'>
                    <Upcoming data={data}/>
                </div>
            </div>
          
        </div>
    )
}

export default WeekGlance;
