import { useState } from "react"



interface Props {
    data: any
}

const color = {
    ARRIVED: "24FF00",
    TRANSPORT_ERROR: "E0000E",
    IN_TRANSIT: "00D6E0",
    ROLL_OVER: "C8E000",
    CANCELLED: "636363",
    CUSTOMS_HOLD: "D78100",
}


const StatusColor= ({data}: Props) => {
    let color: string = "24FF00"
    if (data.status === "ARRIVED"){
        color = "24FF00"
    }


    return(
        <div>
            <div 
                className="daySlot"
                style={{display:"flex" , flexDirection: "row", margin:"0 5px 0 10px", padding:"5px", height: "1vh", aspectRatio: "1/1" , borderRadius: "360px", border: "1px solid black" , backgroundColor: "24FF00"}}>
                
            </div>
        </div>
    )
}

export default StatusColor
