import {  useState } from "react"
import '../App.css'


interface Props {
    data: any,
    todayDynamic: any
}
// ARRIVED: "24FF00",
// //     TRANSPORT_ERROR: "E0000E",
// //     IN_TRANSIT: "00D6E0",
// //     ROLL_OVER: "C8E000",
// //     CANCELLED: "636363",
// //     CUSTOMS_HOLD: "D78100",

const StatusDots= ({data, todayDynamic}: Props) => {
    const [colorStatus, setColorStatus] = useState("636363")

   data.status === "ARRIVED" && setColorStatus("24FF00")

    return (
        <div>
            { 
                data.estimatedArrival === todayDynamic 
                && 
                <div className="daySlot" style={{display:"flex" , flexDirection: "row", margin:"0 5px 0 10px", padding:"5px", height: "1vh", aspectRatio: "1/1" , borderRadius: "360px"}}>
                    {}
                </div>
            }
        </div>

            



        
    )
}

export default StatusDots
