import '../App.css'
const ProgressBar = require('progressbar.js');



interface Props {
    data: any,
    selectedView: any
}


const SelectionDetails= ({data, selectedView}: Props) => {
    let totalShippingDays = (new Date(selectedView.estimatedArrival).getTime()- new Date( selectedView.estimatedDeparture).getTime())/1000/60/60/24;
  

    return (
        <div style={{ height:"100px", display:'flex', width:"100%", flexDirection:"column", }}>
            <div style={{display: "flex", flexDirection:"row", justifyContent:"space-between", }}>
                <h1>{selectedView.client}</h1>
                <h1 style={{opacity:"50%"}}>{selectedView.houseBillNumber}</h1>
            </div>
            <div style={{display: "flex", alignItems:"center"}}>
                <h3>To </h3>
                <p style={{margin:"0 0 0 10px"}}>{selectedView.destination}</p>
                <h3>From </h3>
                <p style={{margin:"0 0 0 10px"}}>{selectedView.origin}</p>
            </div>
            <div style={{height: "20px", width:"100%", border:"1px Solid Black"}}>
                <div></div>
            </div>

            {selectedView.estimatedArrival}
            {selectedView.estimatedDeparture}
            {selectedView.id}
            {selectedView.mode}
            
            {selectedView.status}
        </div>
    )
}

export default SelectionDetails;
