import '../App.css'
import { format } from 'date-fns'
import StatusColor from './StatusColor'



interface Props {
    data: any
}


const Upcoming= ({data}: Props) => {
    const today:any = format(new Date(), "MM/dd/yy")

    let upcomingData = data.filter((e:any,o:number)=>(
        e.estimatedArrival >= today 
    )) 

    console.log(upcomingData[0])

    return (
        <div id='scroll' style={{ margin: "20px 0 0 0 "}} >
                {upcomingData.map((e:any,  i:number)=>{
                    return(
                        <div key={i} style={{width: "100%", display:"flex", padding: "10px",  height:"5vh", borderBottom:"2px solid #80baac", justifyContent:"center"}}>
                            <StatusColor data={e}/>
                            <div style={{display:"flex", flexDirection:"column"}}>
                                <div style={{display:"flex", justifyContent:"space-between"}}> 
                                    {e.status}
                                    {e.houseBillNumber }
                                </div>
                                <div style={{}}>
                                    {e.mode}
                                    {e.estimatedDeparture}
                                    {e.estimatedArrival}
                                </div>
                            </div>
                            
                            
                           
                        </div>
                    )
                })}
        </div>
    )
}   

export default Upcoming;
